import React, { useEffect, useState } from "react";
import "./App.css";
import questions from "./questions";
import Result from "./components/Result";
import QuestionBox from "./components/QuestionBox";



function App() {
  const [theme, setTheme] = useState(true)
  const [themeName, setThemeName] = useState("dark")

  const handleToggle = ()=>{
    setTheme(theme?false:true);
  }

  function backGroundColors(color){

    document.body.style.backgroundColor = color? "#C2B540":"white";
    return{
      backgroundColor : color? "#6e6a6a":"white",
    }
  }



  useEffect(()=>{
    setThemeName(themeName==="Light Mode"?"Dark Mode":"Light Mode")
  },[theme])

  return (
    <div className="body">

      <h1 className="main"style={backGroundColors(theme)}>QUIZZARD</h1>
      <div className="buttons">
      <div className="start">Let's Play</div>
      <div className="mode" onClick={handleToggle}>{themeName}</div>
      </div>
      <QuestionBox/>
    </div>
  );
}

export default App;
