import React from 'react'
import questions from '../questions'
import App from '../App'


export default function QuestionBox() {
  return (
    <div>
      <p className='main'>QUIZZARD</p>
      <div className='box'>
        <div className='question'>
        <span id='quenum'>1.</span>
        <span id='quetext'>questions</span>
        </div>
        <div className='options'>
          {questions[0].options.map((options,i)=>{
            return(
              <button key={i} className='opt_btn'>
                {options.text}
              </button>
            )
          })}
        </div>
       <div>
        <button>Highlight</button>
        <button>Remove Highlight</button>
       </div>


      </div>

  </div>
  )
}
