// export default App;
import React, { useState, useEffect } from "react";
import "./App.css";
import Result from "./components/Result";
import QuestionBox from "./components/QuestionBox";
import questions from "./questions"; // Make sure to import your questions array

function App() {
  const [theme, setTheme] = useState(true);
  const [themeName, setThemeName] = useState("dark");
  const [score, setScore] = useState(0);
  const [showResult, setShowResult] = useState(false);
  const [currentQue, setCurrentQue] = useState(0);

  const Toggle = () => {
    setTheme((prevTheme) => !prevTheme);
  };

  function backGroundColors(color) {
    document.body.style.backgroundColor = color ? "#C2B540" : "black";
    return {
      backgroundColor: color ? "#C2B540" : "black",
    };
  }

  useEffect(() => {
    setThemeName(themeName === "Light" ? "Dark" : "Light");
  }, [theme]);

  const handleScore = (selectedOption) => {
    if (selectedOption === 0) {
      setScore((prevScore) => prevScore + 1);
    }

    if (currentQue === questions.length - 1) {
      setShowResult(true);
    } else {
      setCurrentQue(currentQue + 1);
    }
  };

  const handleRestart = () => {
    setCurrentQue(0);
    setScore(0);
    setShowResult(false);
  };

  return (
    <div>
      <div className="mode" onClick={Toggle}>
        {themeName}
      </div>

      <div className="box" style={backGroundColors(theme)}>
        {!showResult ? (
          <QuestionBox
            heading="hello"
            handleScore={handleScore}
            currentQue={currentQue}
          />
        ) : (
          <Result score={score} handleRestart={handleRestart} />
        )}
      </div>
    </div>
  );
}

export default App;
