import React from 'react'
import questions from '../questions'
import App from '../App'


export default function QuestionBox() {
  return (

      <div className='box'>
      <p className=''>QUIZZARD</p>
        <div className='question'>
        <span id='quenum'>1.</span>
        <span id='quetext'>{questions[0].text}</span>
        </div>
        <div className='options'>
          {questions[0].options.map((options,i)=>{
            return(
              <button key={i} className='opt_btn'>
                {options.text}
              </button>
            )
          })}
        </div>
       <div>
        <button>Highlight</button>
        <button>Remove Highlight</button>
       </div>




  </div>
  )
}
