import React, { useState } from 'react';
import QuestionBox from './QuestionBox';

export default function Result() {
  const [score, setScore] = useState(0);

  const handleScore = (selectedOption) => {

    if (selectedOption === 0) {
      setScore(score + 1);
    }

    // You can continue this logic for other questions

    // After handling the score for all questions, you may want to display the final score
    console.log('Final Score:', score);
  };

  return (
    <div>
      <QuestionBox handleScore={handleScore} />
    </div>
  );
}
