import React, { useState } from 'react';
import questions from '../questions';
import Result from './Result';

export default function QuestionBox() {
  const [currentQue, setCurrentQue] = useState(0);
  const [highlight, setHighlight] = useState(false);
  const [score, setScore] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [resultShow, setResultShow] = useState(false);

  const changeQue = () => {
    scoreUpdate();
    if (currentQue < questions.length - 1) {
      setCurrentQue(currentQue + 1);
      setHighlight(false);
      setSelectedOption(null);
    } else {
      setResultShow(true);
    }
  };

  const handleOptionClick = (index) => {
    setSelectedOption(index + 1);
    changeQue();
  };



  const handleHighlight = () => {
    setHighlight(true);
  };

  const removeHighlight = () => {
    setHighlight(false);
  };

  // const scoreUpdate = () => {
  //   if (selectedOption === questions[currentQue].options.findIndex(option => option.isCorrect) + 1) {
  //     setScore(score + 1);
  //   }
  // };
  const scoreUpdate = () => {
    const correctOptionIndex = questions[currentQue].options.findIndex(option => option.isCorrect);

    if (correctOptionIndex !== -1 && selectedOption === correctOptionIndex + 1) {
      console.log('Selected option is correct!');
      setScore(score + 1);
    } else {
      console.log('Selected option is incorrect.');
    }
  };



  return (
    <div>
      <p className='name'>QUIZZARD</p>
      <div className='container'>
        {resultShow ? (
          <Result score={score} totalScore={questions.length} />
        ) : (
          <>
            <div className='question'>
              <div className='question' style={{ color: highlight ? '#D22727' : 'black' }}>
                <span id='quenum'>Questions: {currentQue + 1} of 5</span>
                <br></br>
                <span id='quetext'>{questions[currentQue].text}</span>
              </div>
            </div>

            <div className='options'>
              {questions[currentQue].options.map((options, i) => (
                <button key={i} className='opt_btn' onClick={() => handleOptionClick(i)}>
                  {options.text}
                </button>
              ))}
            </div>
            <div>
              <button onClick={handleHighlight} className='high'>
                Highlight
              </button>
              <button onClick={removeHighlight} className='high'>
                Remove Highlight
              </button>
            </div>
          </>
        )}
      </div>
    </div>
  );
}
