import React, { useState } from 'react';
import questions from '../questions';
import App from '../App';

export default function QuestionBox() {
  const [currentQue, setCurrentQue] = useState(0);

  const changeQue = () => {
    setCurrentQue(currentQue + 1);
  };

  const handleOptionClick = (optionIndex) => {
    const isCorrect = questions[currentQue].options[optionIndex].correct;

    if (isCorrect) {
      changeQue();
    }
  };

  return (
    <div>
      <p className='name'>QUIZZARD</p>
      <div className='container'>
        <div className='question'>
          <span id='quenum'>{currentQue + 1}.</span>
          <span id='quetext'>{questions[currentQue].text}</span>
        </div>

        {questions[currentQue].options.map((option, i) => {
          return (
            <button
              key={i}
              className='opt_btn'
              onClick={() => handleOptionClick(i)}
            >
              {option.text}
            </button>
          );
        })}

        <div className='high'>
          <button>Highlight</button>
          <button>Remove Highlight</button>
        </div>
      </div>
    </div>
  );
}
